/**
 * Core type definitions for the tactical card game
 * These are the foundational types that everything else builds upon
 */

// ============================================================================
// BASIC ENUMS AND CONSTANTS
// ============================================================================

/** The nine core stats that define a summon's capabilities */
export enum StatType {
  STR = 'STR', // Strength - Affects physical attack damage
  END = 'END', // Endurance - Affects maximum HP
  DEF = 'DEF', // Defense - Affects physical damage taken
  INT = 'INT', // Intelligence - Affects magical attack damage
  SPI = 'SPI', // Spirit - Affects healing and support abilities
  MDF = 'MDF', // Magic Defense - Affects magical damage taken
  SPD = 'SPD', // Speed - Affects movement speed
  ACC = 'ACC', // Accuracy - Affects chance to hit
  LCK = 'LCK'  // Luck - Affects critical hits and random elements
}

/** Growth rate categories for stats when leveling up */
export enum GrowthRate {
  MINIMAL = 'MINIMAL',       // 1 every 2 levels (0.5)
  STEADY = 'STEADY',         // 2 every 3 levels (0.66)
  NORMAL = 'NORMAL',         // 1 every level (1.0)
  GRADUAL = 'GRADUAL',       // 1 + bonus every 3 levels (1.33)
  ACCELERATED = 'ACCELERATED', // 1 + bonus every 2 levels (1.5)
  EXCEPTIONAL = 'EXCEPTIONAL'  // 2 every level (2.0)
}

/** Card rarity levels that affect generation probability and power */
export enum Rarity {
  COMMON = 'COMMON',
  UNCOMMON = 'UNCOMMON',
  RARE = 'RARE',
  EPIC = 'EPIC',
  LEGENDARY = 'LEGENDARY'
}

/** Different types of cards in the game */
export enum CardType {
  SUMMON = 'SUMMON',
  ROLE = 'ROLE',
  EQUIPMENT = 'EQUIPMENT',
  ACTION = 'ACTION',
  BUILDING = 'BUILDING',
  QUEST = 'QUEST',
  COUNTER = 'COUNTER',
  REACTION = 'REACTION',
  ADVANCE = 'ADVANCE'
}

/** Equipment slot types */
export enum EquipmentSlot {
  WEAPON = 'WEAPON',
  OFFHAND = 'OFFHAND',
  ARMOR = 'ARMOR',
  ACCESSORY = 'ACCESSORY'
}

/** Game speed levels for the priority system */
export enum Speed {
  ACTION = 'ACTION',     // Slowest - main phase only
  REACTION = 'REACTION', // Medium - can respond to actions
  COUNTER = 'COUNTER'    // Fastest - can respond to anything
}

/** Damage types for attacks and effects */
export enum DamageType {
  PHYSICAL = 'PHYSICAL',
  MAGICAL = 'MAGICAL'
}

/** Damage attributes for elemental effects */
export enum DamageAttribute {
  NEUTRAL = 'NEUTRAL',
  FIRE = 'FIRE',
  WATER = 'WATER',
  EARTH = 'EARTH',
  AIR = 'AIR',
  LIGHT = 'LIGHT',
  DARK = 'DARK'
}

/** Turn phases */
export enum TurnPhase {
  DRAW = 'DRAW',
  LEVEL = 'LEVEL',
  ACTION = 'ACTION',
  END = 'END'
}

/** Game zones where cards can exist */
export enum Zone {
  HAND = 'HAND',
  MAIN_DECK = 'MAIN_DECK',
  ADVANCE_DECK = 'ADVANCE_DECK',
  DISCARD_PILE = 'DISCARD_PILE',
  RECHARGE_PILE = 'RECHARGE_PILE',
  IN_PLAY = 'IN_PLAY',
  REMOVED = 'REMOVED'
}

// ============================================================================
// COORDINATE AND POSITIONING
// ============================================================================

/** 2D coordinate for game board positioning */
export interface Coordinate {
  x: number;
  y: number;
}

/** Game board dimensions and territory definitions */
export const BOARD_CONFIG = {
  WIDTH: 12,
  HEIGHT: 14,
  PLAYER_TERRITORY_ROWS: 3
} as const;

// ============================================================================
// BASIC INTERFACES
// ============================================================================

/** Base stats object with all nine stats */
export interface Stats {
  [StatType.STR]: number;
  [StatType.END]: number;
  [StatType.DEF]: number;
  [StatType.INT]: number;
  [StatType.SPI]: number;
  [StatType.MDF]: number;
  [StatType.SPD]: number;
  [StatType.ACC]: number;
  [StatType.LCK]: number;
}

/** Growth rates for each stat when leveling */
export interface GrowthRates {
  [StatType.STR]: GrowthRate;
  [StatType.END]: GrowthRate;
  [StatType.DEF]: GrowthRate;
  [StatType.INT]: GrowthRate;
  [StatType.SPI]: GrowthRate;
  [StatType.MDF]: GrowthRate;
  [StatType.SPD]: GrowthRate;
  [StatType.ACC]: GrowthRate;
  [StatType.LCK]: GrowthRate;
}

/** Unique identifier for any game entity */
export type EntityId = string;

/** Base interface for all identifiable game entities */
export interface Entity {
  id: EntityId;
  name: string;
  description?: string;
}

/** Player identification */
export interface Player extends Entity {
  victoryPoints: number;
}

// ============================================================================
// UTILITY TYPES
// ============================================================================

/** Numeric range for validation */
export interface NumericRange {
  min: number;
  max: number;
}

/** Probability percentage (0-100) */
export type Percentage = number;

/** Level range for summons */
export const LEVEL_RANGE: NumericRange = { min: 5, max: 20 };

/** Maximum hand size before discarding */
export const MAX_HAND_SIZE = 6;

/** Victory points needed to win */
export const VICTORY_POINTS_TO_WIN = 3;

/**
 * Card type definitions for all card types in the game
 * This includes the data structures for cards and their effects
 */

import {
  Entity,
  EntityId,
  Rarity,
  CardType,
  EquipmentSlot,
  Speed,
  Stats,
  GrowthRates,
  GrowthRate,
  Percentage
} from './core';

// ============================================================================
// EFFECT SYSTEM TYPES
// ============================================================================

/** Base interface for all card effects */
export interface Effect {
  id: string;
  name: string;
  description: string;
  speed: Speed;
  /** Conditions that must be met to play/activate this effect */
  requirements: EffectRequirement[];
  /** What happens when this effect resolves */
  resolution: EffectResolution;
}

/** Requirement types for playing cards or activating effects */
export interface EffectRequirement {
  type: 'SUMMON_IN_PLAY' | 'SUMMON_ROLE' | 'SUMMON_LEVEL' | 'SUMMON_SPECIES' | 
        'TERRITORY_CONTROL' | 'CARD_IN_HAND' | 'CUSTOM';
  parameters: Record<string, any>;
  description: string;
}

/** What happens when an effect resolves */
export interface EffectResolution {
  type: 'DAMAGE' | 'HEAL' | 'STAT_MODIFIER' | 'MOVEMENT' | 'SUMMON' | 
        'DRAW_CARDS' | 'LEVEL_UP' | 'DESTROY' | 'CUSTOM';
  parameters: Record<string, any>;
  targetType: 'SINGLE' | 'MULTIPLE' | 'ALL' | 'AREA';
}

/** Trigger conditions for automatic effects */
export interface Trigger {
  event: 'ON_PLAY' | 'ON_DEFEAT' | 'ON_DAMAGE' | 'ON_HEAL' | 'ON_MOVE' | 
         'TURN_START' | 'TURN_END' | 'PHASE_START' | 'PHASE_END' | 'CUSTOM';
  conditions?: EffectRequirement[];
  effect: Effect;
}

// ============================================================================
// BASE CARD INTERFACE
// ============================================================================

/** Base interface for all cards */
export interface BaseCard extends Entity {
  cardType: CardType;
  rarity: Rarity;
  /** Visual representation data */
  artwork?: string;
  /** Card effects and abilities */
  effects: Effect[];
  /** Automatic triggers */
  triggers: Trigger[];
  /** Cost to play the card (if applicable) */
  cost?: number;
  /** Tags for categorization and searching */
  tags: string[];
}

// ============================================================================
// SUMMON CARDS
// ============================================================================

/** Species definition for summon generation */
export interface SummonSpecies extends Entity {
  /** Base stats for this species */
  baseStats: Stats;
  /** Visual representation */
  artwork: string;
  /** Species-specific abilities */
  speciesAbilities: Effect[];
}

/** Generated summon card (unique instance) */
export interface SummonCard extends BaseCard {
  cardType: CardType.SUMMON;
  /** Reference to the species template */
  speciesId: EntityId;
  /** Generated stats for this specific summon */
  baseStats: Stats;
  /** Generated growth rates for this specific summon */
  growthRates: GrowthRates;
  /** Generated at pack opening time */
  generatedAt: Date;
}

// ============================================================================
// ROLE CARDS
// ============================================================================

/** Role cards define class/job for summons */
export interface RoleCard extends BaseCard {
  cardType: CardType.ROLE;
  /** Tier level of this role (1 = basic, 2+ = advanced) */
  tier: number;
  /** Stat modifiers applied when this role is active */
  statModifiers: Partial<Stats>;
  /** Role-specific abilities */
  roleAbilities: Effect[];
}

// ============================================================================
// EQUIPMENT CARDS
// ============================================================================

/** Equipment cards enhance summons */
export interface EquipmentCard extends BaseCard {
  cardType: CardType.EQUIPMENT;
  /** Which slot this equipment occupies */
  slot: EquipmentSlot;
  /** Stat bonuses provided */
  statBonuses: Partial<Stats>;
  /** Base power for weapons */
  basePower?: number;
  /** Attack range for weapons */
  attackRange?: number;
  /** Equipment-specific abilities */
  equipmentAbilities: Effect[];
}

// ============================================================================
// ACTION CARDS
// ============================================================================

/** Action cards are played during action phase */
export interface ActionCard extends BaseCard {
  cardType: CardType.ACTION;
  /** Where this card goes after resolution */
  destinationAfterUse: 'DISCARD' | 'RECHARGE' | 'REMOVED';
  /** Main effect of the action */
  actionEffect: Effect;
}

// ============================================================================
// BUILDING CARDS
// ============================================================================

/** Building cards create permanent effects on the board */
export interface BuildingCard extends BaseCard {
  cardType: CardType.BUILDING;
  /** Size requirements for placement */
  dimensions: {
    width: number;
    height: number;
  };
  /** Is this building placed face down initially? */
  isHidden: boolean;
  /** Ongoing effects while in play */
  ongoingEffects: Effect[];
  /** Destruction conditions */
  destructionConditions?: EffectRequirement[];
}

// ============================================================================
// QUEST CARDS
// ============================================================================

/** Quest cards provide objectives for rewards */
export interface QuestCard extends BaseCard {
  cardType: CardType.QUEST;
  /** Objective that must be completed */
  objective: EffectRequirement;
  /** Reward for completing the quest */
  reward: Effect;
  /** Can this quest be completed multiple times? */
  repeatable: boolean;
}

// ============================================================================
// COUNTER AND REACTION CARDS
// ============================================================================

/** Counter cards respond to specific triggers */
export interface CounterCard extends BaseCard {
  cardType: CardType.COUNTER;
  /** Must be set face down before activation */
  mustBeSet: true;
  /** Specific trigger that allows activation */
  activationTrigger: Trigger;
  /** Additional cost to activate */
  activationCost?: Effect;
}

/** Reaction cards provide quick responses */
export interface ReactionCard extends BaseCard {
  cardType: CardType.REACTION;
  /** Can be played from hand or set face down */
  canPlayFromHand: boolean;
  /** Quick response effect */
  reactionEffect: Effect;
}

// ============================================================================
// ADVANCE CARDS
// ============================================================================

/** Advance cards evolve summons to higher tiers */
export interface AdvanceCard extends BaseCard {
  cardType: CardType.ADVANCE;
  /** Requirements for the target summon */
  summonRequirements: EffectRequirement[];
  /** New role granted by advancement */
  newRole: RoleCard;
  /** Additional effects from advancement */
  advancementEffects: Effect[];
}

// ============================================================================
// UNION TYPE FOR ALL CARDS
// ============================================================================

/** Union type representing any card in the game */
export type Card = 
  | SummonCard
  | RoleCard
  | EquipmentCard
  | ActionCard
  | BuildingCard
  | QuestCard
  | CounterCard
  | ReactionCard
  | AdvanceCard;

// ============================================================================
// CARD COLLECTION TYPES
// ============================================================================

/** Player's card collection */
export interface CardCollection {
  playerId: EntityId;
  cards: Map<EntityId, Card>;
  /** Quick lookup by card type */
  cardsByType: Map<CardType, EntityId[]>;
  /** Quick lookup by rarity */
  cardsByRarity: Map<Rarity, EntityId[]>;
}

/** Deck composition for a game */
export interface Deck {
  playerId: EntityId;
  name: string;
  /** The 3 summon slots */
  summonSlots: SummonSlot[];
  /** Main deck cards */
  mainDeck: EntityId[];
  /** Advance deck cards */
  advanceDeck: EntityId[];
  /** When this deck was created/modified */
  lastModified: Date;
}

/** A single summon slot configuration */
export interface SummonSlot {
  /** The primary summon card */
  summon: EntityId;
  /** The role card applied */
  role: EntityId;
  /** Equipment in each slot */
  equipment: {
    [EquipmentSlot.WEAPON]?: EntityId;
    [EquipmentSlot.OFFHAND]?: EntityId;
    [EquipmentSlot.ARMOR]?: EntityId;
    [EquipmentSlot.ACCESSORY]?: EntityId;
  };
}

// ============================================================================
// CARD GENERATION TYPES
// ============================================================================

/** Configuration for generating summon cards */
export interface SummonGenerationConfig {
  species: SummonSpecies;
  rarity: Rarity;
  /** Probability tables for growth rates by rarity */
  growthRateProbabilities: Record<Rarity, Record<GrowthRate, Percentage>>;
}

/** Pack opening configuration */
export interface PackConfig {
  name: string;
  cardCount: number;
  rarityDistribution: Record<Rarity, Percentage>;
  availableSpecies: EntityId[];
  cost: number; // in-game currency
}

/**
 * Game state types for managing the active game session
 * This includes the board state, player state, and turn management
 */

import {
  Entity,
  EntityId,
  Player,
  TurnPhase,
  Zone,
  Coordinate,
  Stats,
  GrowthRates
} from './core';
import { Card, Deck, SummonSlot } from './cards';

// ============================================================================
// SUMMON UNIT (IN-PLAY SUMMON)
// ============================================================================

/** A summon unit that exists on the game board */
export interface SummonUnit extends Entity {
  /** Reference to the original summon card */
  originalCardId: EntityId;
  /** Current owner */
  playerId: EntityId;
  /** Current position on the board */
  position: Coordinate;
  /** Current level (5-20) */
  level: number;
  /** Current HP */
  currentHp: number;
  /** Maximum HP at current level */
  maxHp: number;
  /** Current calculated stats */
  currentStats: Stats;
  /** Base stats from the original card */
  baseStats: Stats;
  /** Growth rates from the original card */
  growthRates: GrowthRates;
  /** Currently equipped role */
  currentRole: EntityId;
  /** Currently equipped items */
  equipment: {
    weapon?: EntityId;
    offhand?: EntityId;
    armor?: EntityId;
    accessory?: EntityId;
  };
  /** Active status effects */
  statusEffects: StatusEffect[];
  /** Movement used this turn */
  movementUsed: number;
  /** Has attacked this turn */
  hasAttacked: boolean;
  /** Temporary modifiers from card effects */
  temporaryModifiers: TemporaryModifier[];
}

/** Status effects that can be applied to summon units */
export interface StatusEffect {
  id: string;
  name: string;
  description: string;
  /** How many turns remaining */
  duration: number;
  /** Stat modifiers */
  statModifiers?: Partial<Stats>;
  /** Special conditions */
  conditions?: {
    immobilized?: boolean;
    cannotAttack?: boolean;
    cannotUseAbilities?: boolean;
  };
  /** Source of this effect */
  sourceId: EntityId;
}

/** Temporary modifiers from card effects */
export interface TemporaryModifier {
  id: string;
  name: string;
  /** When this modifier expires */
  expiresAt: 'END_OF_TURN' | 'END_OF_OPPONENT_TURN' | 'PERMANENT' | number;
  /** Stat changes */
  statModifiers?: Partial<Stats>;
  /** Other property changes */
  propertyModifiers?: Record<string, any>;
  /** Source of this modifier */
  sourceId: EntityId;
}

// ============================================================================
// BOARD STATE
// ============================================================================

/** A space on the game board */
export interface BoardSpace {
  coordinate: Coordinate;
  /** Which player controls this territory (if any) */
  controlledBy?: EntityId;
  /** Summon unit occupying this space (if any) */
  occupiedBy?: EntityId;
  /** Buildings covering this space */
  buildings: EntityId[];
  /** Is this space valid for summon placement */
  isValidSummonSpace: boolean;
  /** Special terrain effects */
  terrainEffects?: string[];
}

/** The complete game board state */
export interface GameBoard {
  /** 12x14 grid of spaces */
  spaces: BoardSpace[][];
  /** Active summon units on the board */
  summonUnits: Map<EntityId, SummonUnit>;
  /** Active buildings on the board */
  buildings: Map<EntityId, ActiveBuilding>;
}

/** A building that has been played and is active on the board */
export interface ActiveBuilding extends Entity {
  /** Reference to the original building card */
  originalCardId: EntityId;
  /** Player who controls this building */
  playerId: EntityId;
  /** Spaces this building occupies */
  occupiedSpaces: Coordinate[];
  /** Is this building face down (hidden)? */
  isHidden: boolean;
  /** Turn when this building was played */
  playedOnTurn: number;
  /** Active timers for destruction */
  destructionTimers?: {
    type: 'TURN_COUNT' | 'CONDITION';
    value: number;
  }[];
}

// ============================================================================
// PLAYER GAME STATE
// ============================================================================

/** A player's state during an active game */
export interface PlayerGameState {
  player: Player;
  /** Cards in different zones */
  zones: {
    [Zone.HAND]: EntityId[];
    [Zone.MAIN_DECK]: EntityId[];
    [Zone.ADVANCE_DECK]: EntityId[];
    [Zone.DISCARD_PILE]: EntityId[];
    [Zone.RECHARGE_PILE]: EntityId[];
    [Zone.IN_PLAY]: EntityId[];
    [Zone.REMOVED]: EntityId[];
  };
  /** Original deck configuration */
  originalDeck: Deck;
  /** Summon slots as configured in deck */
  summonSlots: SummonSlot[];
  /** Which summon slots have been played this game */
  playedSummonSlots: boolean[];
  /** Has used turn summon this turn */
  hasUsedTurnSummon: boolean;
  /** Face-down cards in play (counters, reactions, etc.) */
  faceDownCards: Map<EntityId, Coordinate>;
}

// ============================================================================
// STACK SYSTEM
// ============================================================================

/** An action waiting to resolve on the stack */
export interface StackEntry {
  id: string;
  /** Player who initiated this action */
  playerId: EntityId;
  /** Type of action */
  actionType: 'PLAY_CARD' | 'ACTIVATE_ABILITY' | 'ATTACK' | 'MOVE' | 'TRIGGER_EFFECT';
  /** Source card or ability */
  sourceId: EntityId;
  /** Target(s) of this action */
  targets: ActionTarget[];
  /** Speed level of this action */
  speed: import('./core').Speed;
  /** Additional parameters */
  parameters: Record<string, any>;
  /** When this was added to the stack */
  timestamp: number;
}

/** Target for an action */
export interface ActionTarget {
  type: 'SUMMON_UNIT' | 'CARD' | 'PLAYER' | 'COORDINATE' | 'BUILDING';
  id?: EntityId;
  coordinate?: Coordinate;
  parameters?: Record<string, any>;
}

/** The game's action stack */
export interface ActionStack {
  entries: StackEntry[];
  /** Is the stack currently resolving */
  isResolving: boolean;
  /** Current priority holder */
  priorityPlayerId: EntityId;
}

// ============================================================================
// TURN MANAGEMENT
// ============================================================================

/** Turn state management */
export interface TurnState {
  /** Current turn number */
  turnNumber: number;
  /** Active player */
  activePlayerId: EntityId;
  /** Current phase */
  currentPhase: TurnPhase;
  /** Phase-specific data */
  phaseData?: Record<string, any>;
  /** Actions taken this turn */
  actionsTaken: StackEntry[];
}

// ============================================================================
// COMPLETE GAME STATE
// ============================================================================

/** The complete state of an active game */
export interface GameState {
  /** Unique game identifier */
  gameId: EntityId;
  /** Players in this game */
  players: Map<EntityId, PlayerGameState>;
  /** The game board */
  board: GameBoard;
  /** Action stack */
  stack: ActionStack;
  /** Turn management */
  turn: TurnState;
  /** All cards used in this game */
  gameCards: Map<EntityId, Card>;
  /** Game configuration */
  config: GameConfig;
  /** Game status */
  status: GameStatus;
  /** When the game started */
  startedAt: Date;
  /** Game history for replay */
  history: GameEvent[];
}

/** Game configuration settings */
export interface GameConfig {
  /** Game format (currently only 3v3 supported) */
  format: '3v3';
  /** Time limits */
  timeLimits?: {
    turnTimeLimit: number; // seconds
    gameTimeLimit: number; // seconds
  };
  /** Rule variations */
  rules: {
    allowSpectators: boolean;
    allowUndo: boolean;
    allowPause: boolean;
  };
}

/** Overall game status */
export interface GameStatus {
  state: 'WAITING' | 'IN_PROGRESS' | 'COMPLETED' | 'PAUSED' | 'ABANDONED';
  /** Winner if game is completed */
  winnerId?: EntityId;
  /** Reason for game end */
  endReason?: 'VICTORY_POINTS' | 'TIMEOUT' | 'FORFEIT' | 'DRAW';
  /** Current scores */
  scores: Map<EntityId, number>;
}

/** Events that occur during the game for history/replay */
export interface GameEvent {
  id: string;
  timestamp: Date;
  playerId: EntityId;
  eventType: 'CARD_PLAYED' | 'ATTACK' | 'MOVE' | 'ABILITY_ACTIVATED' | 
             'PHASE_CHANGE' | 'TURN_CHANGE' | 'GAME_END';
  details: Record<string, any>;
  /** Stack state when this event occurred */
  stackSnapshot?: StackEntry[];
}

/**
 * Calculation and formula types for game mechanics
 * This includes damage calculations, stat calculations, and other formulas
 */

import { Stats, GrowthRate, Percentage, StatType } from './core';
import { SummonUnit } from './game-state';

// ============================================================================
// STAT CALCULATION TYPES
// ============================================================================

/** Configuration for calculating a summon's stats at a given level */
export interface StatCalculationConfig {
  baseStat: number;
  growthRate: GrowthRate;
  level: number;
  roleModifier: number;
  equipmentBonus: number;
  temporaryModifiers: number;
}

/** Result of a stat calculation */
export interface StatCalculationResult {
  finalValue: number;
  breakdown: {
    base: number;
    growth: number;
    role: number;
    equipment: number;
    temporary: number;
  };
}

/** Growth rate numeric values for calculations */
export const GROWTH_RATE_VALUES: Record<GrowthRate, number> = {
  [GrowthRate.MINIMAL]: 0.5,      // 1 every 2 levels
  [GrowthRate.STEADY]: 0.66,      // 2 every 3 levels  
  [GrowthRate.NORMAL]: 1.0,       // 1 every level
  [GrowthRate.GRADUAL]: 1.33,     // 1 + bonus every 3 levels
  [GrowthRate.ACCELERATED]: 1.5,  // 1 + bonus every 2 levels
  [GrowthRate.EXCEPTIONAL]: 2.0   // 2 every level
};

// ============================================================================
// DERIVED STAT CALCULATIONS
// ============================================================================

/** Configuration for calculating derived stats like HP, movement, etc. */
export interface DerivedStatConfig {
  /** Base value before modifications */
  baseValue: number;
  /** Primary stat that influences this derived stat */
  primaryStat: StatType;
  /** Formula type to use */
  formulaType: 'LINEAR' | 'QUADRATIC' | 'EXPONENTIAL' | 'CUSTOM';
  /** Additional parameters for the formula */
  parameters: Record<string, number>;
}

/** Formulas for calculating derived stats */
export interface DerivedStatFormulas {
  /** Max HP = 50 + Floor(END ^ 1.5) */
  maxHp: (endurance: number) => number;
  /** Movement Speed = 2 + Floor((SPD - 10) / 5) */
  movementSpeed: (speed: number) => number;
  /** Basic Attack To Hit = 90 + (ACC / 10) */
  basicAttackToHit: (accuracy: number) => number;
  /** Critical Hit Chance = Floor((LCK * 0.3375) + 1.65) */
  criticalHitChance: (luck: number) => number;
}

// ============================================================================
// COMBAT CALCULATION TYPES
// ============================================================================

/** Parameters for damage calculation */
export interface DamageCalculationParams {
  /** The attacking unit or effect source */
  attacker: SummonUnit | EffectSource;
  /** The target being damaged */
  target: SummonUnit;
  /** Base power of the attack/effect */
  basePower: number;
  /** Type of damage */
  damageType: 'PHYSICAL' | 'MAGICAL';
  /** Elemental attribute */
  attribute: 'NEUTRAL' | 'FIRE' | 'WATER' | 'EARTH' | 'AIR' | 'LIGHT' | 'DARK';
  /** Custom damage formula (overrides default) */
  customFormula?: string;
  /** Can this attack crit? */
  canCrit: boolean;
  /** Crit multiplier if it crits */
  critMultiplier: number;
}

/** Source of an effect that isn't a summon unit */
export interface EffectSource {
  id: string;
  name: string;
  /** Effective stats for calculation purposes */
  effectiveStats: Partial<Stats>;
  /** Source type */
  sourceType: 'CARD_EFFECT' | 'BUILDING' | 'ENVIRONMENTAL';
}

/** Result of a damage calculation */
export interface DamageCalculationResult {
  /** Final damage amount */
  finalDamage: number;
  /** Did the attack hit? */
  didHit: boolean;
  /** Did the attack crit? */
  didCrit: boolean;
  /** Breakdown of the calculation */
  breakdown: {
    baseDamage: number;
    attackerStat: number;
    defenderStat: number;
    critMultiplier: number;
    otherModifiers: number;
  };
  /** Hit chance that was rolled against */
  hitChance: Percentage;
  /** Crit chance that was rolled against */
  critChance: Percentage;
  /** Random values that were rolled */
  rolls: {
    hitRoll: number;
    critRoll: number;
  };
}

/** Parameters for healing calculation */
export interface HealingCalculationParams {
  /** The healing source */
  healer: SummonUnit | EffectSource;
  /** The target being healed */
  target: SummonUnit;
  /** Base power of the healing */
  basePower: number;
  /** Custom healing formula */
  customFormula?: string;
  /** Can this heal crit? */
  canCrit: boolean;
  /** Crit multiplier if it crits */
  critMultiplier: number;
}

/** Result of a healing calculation */
export interface HealingCalculationResult {
  /** Final healing amount */
  finalHealing: number;
  /** Did the heal crit? */
  didCrit: boolean;
  /** Breakdown of the calculation */
  breakdown: {
    baseHealing: number;
    healerStat: number;
    critMultiplier: number;
    otherModifiers: number;
  };
  /** Actual HP restored (accounting for max HP) */
  actualHpRestored: number;
}

// ============================================================================
// ACCURACY AND HIT CALCULATION
// ============================================================================

/** Parameters for hit chance calculation */
export interface HitCalculationParams {
  /** Base accuracy of the attack/effect */
  baseAccuracy: number;
  /** Attacker's accuracy stat */
  attackerAccuracy: number;
  /** Target's evasion modifiers */
  targetEvasion?: number;
  /** Custom hit formula */
  customFormula?: string;
  /** Environmental modifiers */
  environmentModifiers?: number;
}

/** Standard hit calculation formulas */
export interface HitFormulas {
  /** Standard: base_accuracy + (attacker.ACC / 10) */
  standard: (params: HitCalculationParams) => number;
  /** Enhanced: base_accuracy + (attacker.ACC / 10) + (attacker.LCK / 10) */
  enhanced: (params: HitCalculationParams) => number;
  /** Always hits */
  automatic: (params: HitCalculationParams) => 100;
  /** Custom formula evaluator */
  custom: (formula: string, params: HitCalculationParams) => number;
}

// ============================================================================
// FORMULA EVALUATION SYSTEM
// ============================================================================

/** Context for evaluating custom formulas */
export interface FormulaContext {
  /** The caster/source of the effect */
  caster?: SummonUnit;
  /** The target of the effect */
  target?: SummonUnit;
  /** Additional parameters */
  parameters: Record<string, any>;
  /** Current game state references */
  gameContext: {
    turnNumber: number;
    activePlayerId: string;
    boardState: any; // Reference to board for positional calculations
  };
}

/** A parsed and executable formula */
export interface ParsedFormula {
  /** Original formula string */
  original: string;
  /** Parsed tokens */
  tokens: FormulaToken[];
  /** Variables referenced in the formula */
  variables: string[];
  /** Execute this formula with given context */
  execute: (context: FormulaContext) => number;
}

/** Token types for formula parsing */
export interface FormulaToken {
  type: 'NUMBER' | 'VARIABLE' | 'OPERATOR' | 'FUNCTION' | 'PARENTHESIS';
  value: string | number;
  position: number;
}

/** Available functions in formulas */
export interface FormulaFunctions {
  /** Mathematical functions */
  floor: (value: number) => number;
  ceil: (value: number) => number;
  round: (value: number) => number;
  min: (a: number, b: number) => number;
  max: (a: number, b: number) => number;
  abs: (value: number) => number;
  
  /** Game-specific functions */
  distance: (pos1: {x: number, y: number}, pos2: {x: number, y: number}) => number;
  random: (min: number, max: number) => number;
  summonCount: (playerId: string) => number;
}

// ============================================================================
// PROBABILITY AND RANDOM GENERATION
// ============================================================================

/** Configuration for random generation with weights */
export interface WeightedRandomConfig<T> {
  options: Array<{
    value: T;
    weight: number;
  }>;
}

/** Random number generation context */
export interface RandomContext {
  /** Seed for deterministic randomness (useful for replays) */
  seed?: number;
  /** Source of the randomness for tracking */
  source: string;
  /** Additional context */
  context?: Record<string, any>;
}

/** Result of a random roll */
export interface RandomResult {
  /** The value that was rolled */
  value: number;
  /** The range it was rolled in */
  range: {
    min: number;
    max: number;
  };
  /** Context information */
  context: RandomContext;
  /** Timestamp of the roll */
  timestamp: Date;
}

