      [StatType.MDF]: GrowthRate.STEADY,     // 0.66
      [StatType.SPD]: GrowthRate.MINIMAL,    // 0.5
      [StatType.ACC]: GrowthRate.STEADY,     // 0.66
      [StatType.LCK]: GrowthRate.EXCEPTIONAL // 2.0
